import React, { useState, useEffect} from 'react'
import { Card, Icon,  } from 'semantic-ui-react'
import Logo from '../images/elliot.png';


function ProfileCard(props) {

    const [name, setName] = useState('');
    const [userName, setUserName] = useState('');
    const [followers, setFollowers] = useState('');
    const [avatar, setAvatar] = useState('');
    const [userInput, setUserInput] = useState('');
    const [error, setError] = useState('');
    const [repos, setRepos] = useState('');


    useEffect(() => {
        fetch("https://api.github.com/repos/octokit/core.js/releases/latest", {
            headers: {
                authorization: "token dd68bb632d4ffbbed4b01e1592aa5b3e44e8dcc7"
            }
        })
            .then(response => response.json())
            .then(data => {
                setData(data);
                console.log(data);
            });
    }, []);

    avatar_url: "https://avatars2.githubusercontent.com/in/15368?v=4"
gravatar_id: ""
url: "https://api.github.com/users/github-actions%5Bbot%5D"
html_url: "https://github.com/apps/github-actions"
followers_url: "https://api.github.com/users/github-actions%5Bbot%5D/followers"
following_url: "https://api.github.com/users/github-actions%5Bbot%5D/following{/other_user}"
gists_url: "https://api.github.com/users/github-actions%5Bbot%5D/gists{/gist_id}"
starred_url: "https://api.github.com/users/github-actions%5Bbot%5D/starred{/owner}{/repo}"
subscriptions_url: "https://api.github.com/users/github-actions%5Bbot%5D/subscriptions"
organizations_url: "https://api.github.com/users/github-actions%5Bbot%5D/orgs"
repos_url: "https://api.github.com/users/github-actions%5Bbot%5D/repos"
events_url: "https://api.github.com/users/github-actions%5Bbot%5D/events{/privacy}"

    const setData = ({
        name,
        userName,
        followers_url,
        following_url,
        avatar_url,
        userInput,
        error,
        repos_url,
    }) => {
        setName(name);
        setAvatar(avatar_url);
        setFollowers(followers_url);
        setUserName(userName);
        setUserInput(userInput);
        setRepos(repos_url);
        setError(error);
    };


    const extra = () => (
        <a>
          <Icon name='user' />
          {followers}
        </a>
    )

    return (
        <Card
        image={avatar}
        header={name}
        meta='Friend'
        description='Elliot is a sound engineer living in Nashville who enjoys playing guitar and hanging with his cat.'
        extra={extra(followers)}
      />
    )
  }

export default ProfileCard
