import React, { useState, useEffect} from 'react'
import { Card, Icon,  } from 'semantic-ui-react'
import Logo from '../images/elliot.png';


function ProfileCard(props) {
    // const [data, setData] = useState('');
    const [name, setName] = useState('');
    const [login, setUserName] = useState('');
    const [followers_url, setFollowersUrl] = useState('');
    const [events_url, setEventUrl] = useState('');
    const [following_url, setFollowingUrl] = useState('');
    const [subscriptions_url, setSubscriptionUrls] = useState('');
    const [avatar_url, setAvatar] = useState('');
    const [userInput, setUserInput] = useState('');
    const [error, setError] = useState('');
    const [repos_url, setRepos] = useState('');


    useEffect(() => {
        fetch("https://api.github.com/repos/octokit/core.js/releases/latest", {
            headers: {
                authorization: "token dd68bb632d4ffbbed4b01e1592aa5b3e44e8dcc7"
            }
        })
            .then(response => response.json())
            .then(data => {
                setData(data);
                console.log(data);
            });
    }, []);


    const setData = ({
        name,
        login,
        followers_url,
        following_url,
        subscriptions_url,
        events_url,
        avatar_url,
        userInput,
        error,
        repos_url,
    }) => {
        setName(name);
        setAvatar(avatar_url);
        setFollowersUrl(followers_url);
        setFollowingUrl(following_url);
        setSubscriptionUrls(subscriptions_url);
        setUserName(login);
        setUserInput(userInput);
        setRepos(repos_url);
        setError(error);
    };


    const extra = (props) => (
        <a>
          <Icon name='user' />
          {props.following_url}
        </a>
    )

    return (
        <div>
            <div class="row">
                <div className="col m5">
                <div className="card">
  <div className="card-image waves-effect waves-block waves-light">
    <img className="activator" src="images/office.jpg" />
  </div>
  <div className="card-content">
    <span className="card-title activator grey-text text-darken-4">Card Title<i className="material-icons right">more_vert</i></span>
    <p><a href="#">This is a link</a></p>
  </div>
  <div className="card-reveal">
    <span className="card-title grey-text text-darken-4">Card Title<i className="material-icons right">close</i></span>
    <p>Here is some more information about this product that is only revealed once clicked on.</p>
  </div>
</div>

                </div>
            </div>
                    <Card
        image={avatar_url}
        header={login}
        meta='Friend'
        description='Elliot is a sound engineer living in Nashville who enjoys playing guitar and hanging with his cat.'
        extra={extra({following_url})}
      />
        </div>

    )
  }

export default ProfileCard
