import React, { useState, useEffect} from 'react'
import { Card, Icon,  } from 'semantic-ui-react'
import Logo from '../images/elliot.png';


function ProfileCard(props) {
    // const [data, setData] = useState('');
    const [name, setName] = useState('');
    const [login, setUserName] = useState('');
    const [followers_url, setFollowersUrl] = useState('');
    const [events_url, setEventUrl] = useState('');
    const [following_url, setFollowingUrl] = useState('');
    const [bio, setBio] = useState('');
    const [avatar_url, setAvatar] = useState('');
    const [userInput, setUserInput] = useState('');
    const [error, setError] = useState('');
    const [repos_url, setRepos] = useState('');

    const access_token='e72e16c7e42f292c6912e7710c838347ae178b4a&token_type=bearer'

    useEffect(() => {
        fetch("https://api.github.com/user", {
            headers: {
                Accept: application/json,

                "access_token":"e72e16c7e42f292c6912e7710c838347ae178b4a",
            }
        })
            .then(response => response.json())
            .then(data => {
                setData(data);
                console.log(data);
            });
    }, []);


    const setData = ({
        name,
        login,
        followers_url,
        following_url,
        bio,
        events_url,
        avatar_url,
        userInput,
        error,
        repos_url,
    }) => {
        setName(name);
        setAvatar(avatar_url);
        setFollowersUrl(followers_url);
        setFollowingUrl(following_url);
        setBio(bio);
        setUserName(login);
        setUserInput(userInput);
        setRepos(repos_url);
        setError(error);
    };


    return (
        <div>
            <div class="row">
                <div className="col m5">
                <div className="card">
                    <div className="card-image waves-effect waves-block waves-light">
                        <img className="activator" src={Logo} />
                    </div>
                    <div className="card-content">
                        <span className="card-title activator grey-text text-darken-4">Dieumerci Kazadi<i className="material-icons right">more_vert</i></span>
                            <p> <i class="material-icons">group</i>: {following_url}</p>
                            <p><i class="material-icons">history</i>: {followers_url}</p>
                    </div>
                    <div className="card-reveal">
                        <span className="card-title grey-text text-darken-4">Card Title<i className="material-icons right">close</i></span>
                        <p>Here is some more information about this product that is only revealed once clicked on.</p>
                    </div>
                    </div>
                </div>
            </div>
        </div>

    )
  }

export default ProfileCard
